import React, { Component } from "react";
import "./DoList.css";

export default class DoList extends Component {
  handleAddCard() {
    // var li = document.createElement("li");
    // var inputValue = document.getElementById("myInput").value;
    // var t = document.createTextNode(inputValue);
    // li.appendChild(t);
  }

  render() {
    return (
      <div className="dolist">
        <p className="dolist__title">{this.props.name}</p>
        <textarea
          className="contact-main__form-box contact-main__form-box--large"
          type="text"
          id="message"
          name="message"
          placeholder="what's on your mind"
        ></textarea>
        <button onClick={this.handleAddCard} className="dolist__addcardbutton">
          + Add card
        </button>
      </div>
    );
  }
}
