import React, { Component } from "react";
import "./DoList.css";

export default class DoList extends Component {
  handleAddCard() {
    const newCard = document.createElement("textarea");
    console.log(this.props.myId);
    const myList = document.getElementById(this.props.myId);
    newCard.classList.add("dolist__card");
    myList.appendChild(newCard);
  }

  render() {
    return (
      <div className="dolist">
        <p className="dolist__title">{this.props.name}</p>
        <div className="card-list">
          <textarea className="dolist__card"></textarea>
        </div>
        <button onClick={this.handleAddCard} className="dolist__addcardbutton">
          + Add card
        </button>
      </div>
    );
  }
}
